快速排序
AcWing 785. 快速排序

#include&lt;iostream&gt;using namespace std;const int N = 1e6+10;int q[N];void quick_sort(int q[], int l, int r)&#123;    if(l&gt;=r) return;    int i = l - 1, j = r + 1, x = q[l + r &gt;&gt; 1];    while(i &lt; j)&#123;        do i++; while(q[i] &lt; x);        do j--; while(q[j] &gt; x);        if(i&lt;j) swap(q[i], q[j]);    &#125;    quick_sort(q, l, j);    quick_sort(q, j + 1, r);&#125;int main()&#123;    int n;    scanf(&quot;%d&quot;, &amp;n);    for(int i=0;i&lt;n;i++) scanf(&quot;%d&quot;, &amp;q[i]);    quick_sort(q, 0, n-1);    for(int i=0;i&lt;n;i++) printf(&quot;%d &quot;, q[i]);&#125;




AcWing 786. 第k个数

#include&lt;iostream&gt;using namespace std;const int N = 1e6 + 10;int q[N];void quick_sort(int q[], int l, int r)&#123;    if(l &gt;=r) return;    int i = l-1, j = r + 1, x = q[l + r&gt;&gt;1];    while(i&lt;j)&#123;        do i++; while(q[i] &lt; x);        do j--; while(q[j] &gt; x);        if(i&lt;j) swap(q[i], q[j]);    &#125;    quick_sort(q, l, j), quick_sort(q, j + 1, r);&#125;int main()&#123;        int n,k;    scanf(&quot;%d%d&quot;, &amp;n, &amp;k);        for(int i = 0; i &lt; n; i ++ ) scanf(&quot;%d&quot;, &amp;q[i]);        quick_sort(q, 0, n-1);    printf(&quot;%d&quot;, q[k-1]);        return 0;&#125;



归并排序
AcWing 787. 归并排序 

#include&lt;iostream&gt;using namespace std;const int N = 1e6+10;int q[N], tmp[N];void merge_sort(int q[], int l, int r)&#123;    if(l &gt;=r) return;    int mid = l+r&gt;&gt;1;    merge_sort(q, l, mid);    merge_sort(q, mid+1, r);        int k=0, i=l, j=mid+1;    while(i&lt;=mid &amp;&amp; j&lt;=r)&#123;        if(q[i] &lt;= q[j])             tmp[k++] = q[i++];        else            tmp[k++] = q[j++];    &#125;    while(i&lt;=mid) tmp[k++] = q[i++];    while(j&lt;=r) tmp[k++] = q[j++];    for(int i=l, j=0;i&lt;=r;i++,j++) q[i] = tmp[j];&#125;int main()&#123;        int n;    scanf(&quot;%d&quot;, &amp;n);    for(int i=0;i&lt;n;i++) scanf(&quot;%d&quot;, &amp;q[i]);        merge_sort(q, 0, n-1);        for(int i=0;i&lt;n;i++) printf(&quot;%d &quot;, q[i]);        return 0;&#125;


AcWing 788. 逆序对的数量

#include&lt;iostream&gt;using namespace std;const int N = 1e6+10;int n;int q[N], tmp[N];typedef long long LL;LL merge_sort(int l, int r)&#123;    if(l&gt;=r) return 0;    int mid = l+r&gt;&gt;1;    LL res = merge_sort(l, mid) + merge_sort(mid+1, r);    int k=0, i=l,j=mid+1;    while(i&lt;=mid &amp;&amp; j&lt;=r)&#123;        if(q[i]&lt;=q[j])            tmp[k++] = q[i++];        else&#123;            tmp[k++] = q[j++];            res += mid - i + 1;        &#125;    &#125;    while(i&lt;=mid) tmp[k++] = q[i++];    while(j&lt;=r) tmp[k++] = q[j++];    for(int i=l, j=0;i&lt;=r;i++,j++) q[i] = tmp[j];    return res;&#125;int main()&#123;    cin &gt;&gt; n;    for(int i=0;i&lt;n;i++) cin&gt;&gt;q[i];    cout &lt;&lt;  merge_sort(0, n-1) &lt;&lt; endl;    return 0;&#125;





二分
AcWing 789. 数的范围 

#include&lt;iostream&gt;using namespace std;const int N=1e6+10;int q[N];int main()&#123;        int n, m;    scanf(&quot;%d%d&quot;, &amp;n, &amp;m);    for(int i=0;i&lt;n;i++) cin &gt;&gt; q[i];        while(m--)&#123;        int x;        cin &gt;&gt; x;                int l = 0, r = n - 1;        while(l &lt; r)&#123;            int mid = l + r &gt;&gt; 1;            if(q[mid] &gt;= x) r = mid;            else                l = mid + 1;        &#125;        if(q[l] != x)            cout &lt;&lt; &quot;-1&quot; &lt;&lt; &quot; &quot;;        else            cout &lt;&lt; l &lt;&lt; &quot; &quot;;                l = 0, r = n - 1;        while(l &lt; r)&#123;            int mid = l + r + 1 &gt;&gt; 1;            if(q[mid] &lt;= x) l = mid;            else                r = mid - 1;        &#125;        if(q[r] != x)            cout &lt;&lt; &quot;-1&quot; &lt;&lt; endl;        else            cout &lt;&lt; l &lt;&lt; endl;    &#125;        return 0;&#125;




AcWing 790. 数的三次方根

#include&lt;iostream&gt;using namespace std;int main()&#123;        double n;    cin &gt;&gt; n;            double l = -10000, r = 10000;    while(r - l &gt; 1e-8)&#123;        double mid = (l + r) / 2 ;        if(mid * mid * mid &gt;= n) r = mid;        else            l = mid;    &#125;    printf(&quot;%.6f&quot;, l);    return 0;&#125;



高精度
AcWing 791. 高精度加法 

#include&lt;iostream&gt;#include&lt;vector&gt;using namespace std;vector&lt;int&gt; add(vector&lt;int&gt;&amp;A, vector&lt;int&gt;&amp;B)&#123;    vector&lt;int&gt;C;    int t = 0;    for(int i = 0; i &lt; A.size() || i &lt; B.size(); i++)&#123;        if(i &lt; A.size()) t += A[i];        if(i &lt; B.size()) t += B[i];        C.push_back(t % 10);        t /= 10;    &#125;    if(t) C.push_back(t);    return C;&#125;int main()&#123;    string a, b;    cin &gt;&gt; a &gt;&gt; b;    vector&lt;int&gt;A,B;    for(int i = a.size() - 1; i &gt;= 0; i--) A.push_back(a[i] - &#x27;0&#x27;);    for(int i = b.size() - 1; i &gt;= 0; i--) B.push_back(b[i] - &#x27;0&#x27;);        vector&lt;int&gt; C = add(A, B);        for(int i = C.size() - 1; i &gt;= 0; i--) printf(&quot;%d&quot;, C[i]);        return 0;&#125;




AcWing 792. 高精度减法 

#include&lt;iostream&gt;#include&lt;vector&gt;using namespace std;// A 是否大于 Bbool cmp(vector&lt;int&gt;&amp;A, vector&lt;int&gt;&amp;B)&#123;    if(A.size() != B.size()) return A.size() &gt; B.size();    for(int i = A.size() - 1; i &gt;= 0; i--)&#123;        if(A[i] != B[i])            return A[i] &gt; B[i];    &#125;    return true;&#125;vector&lt;int&gt; sub(vector&lt;int&gt;&amp;A, vector&lt;int&gt;&amp;B)&#123;    vector&lt;int&gt; C;    int t = 0;    for(int i = 0; i &lt; A.size(); i++)&#123;        t = A[i] - t;        if(i &lt; B.size()) t -= B[i];        C.push_back((t + 10) % 10);        if(t &lt; 0) t = 1;        else            t = 0;    &#125;    //删除先导0    while(C.size() &gt; 1 &amp;&amp; C.back() == 0) C.pop_back();    return C;&#125;int main()&#123;    string a, b;    cin &gt;&gt; a &gt;&gt; b;    vector&lt;int&gt;A,B;    for(int i = a.size() - 1; i &gt;= 0; i--) A.push_back(a[i] - &#x27;0&#x27;);    for(int i = b.size() - 1; i &gt;= 0; i--) B.push_back(b[i] - &#x27;0&#x27;);        if(cmp(A, B))&#123;        vector&lt;int&gt; C = sub(A, B);        for(int i = C.size() - 1; i &gt;= 0; i--) printf(&quot;%d&quot;, C[i]);    &#125;else&#123;        vector&lt;int&gt; C = sub(B, A);        printf(&quot;-&quot;);        for(int i = C.size() - 1; i &gt;= 0; i--) printf(&quot;%d&quot;, C[i]);    &#125;    return 0;&#125;




AcWing 793. 高精度乘法

//高精度乘法#include&lt;iostream&gt;#include&lt;vector&gt;using namespace std;vector&lt;int&gt; mul(vector&lt;int&gt;&amp;A, int b)&#123;    vector&lt;int&gt;C;    int t = 0;    for(int i = 0;i&lt;A.size();i++)&#123;        t += A[i] * b;        C.push_back(t % 10);        t /= 10;    &#125;    if(t) C.push_back(t);        // 删除前导0    while(C.size() &gt; 1 &amp;&amp; C.back() == 0) C.pop_back();        return C;&#125;int main()&#123;    string a;    int B;    cin &gt;&gt; a &gt;&gt; B;    vector&lt;int&gt; A;    for(int i = a.size()-1;i&gt;=0;i--) A.push_back(a[i] - &#x27;0&#x27;);        vector&lt;int&gt; C = mul(A, B);        for(int i = C.size()-1;i&gt;=0;i--) cout &lt;&lt; C[i];    &#125;




AcWing 794. 高精度除法

#include&lt;iostream&gt;#include&lt;algorithm&gt;using namespace std;vector&lt;int&gt; div(vector&lt;int&gt;&amp;A, int b, int &amp;r)&#123;    vector&lt;int&gt; C;    for(int i = A.size()-1; i&gt;=0; i--)&#123;        r = r * 10 + A[i];        // cout &lt;&lt; &quot;r: &quot; &lt;&lt; r &lt;&lt; endl;        C.push_back(r / b);        r %= b;    &#125;    // for(auto x:C) cout &lt;&lt; x;        reverse(C.begin(), C.end());    //删除前导0    while(C.size() &gt; 1 &amp;&amp; C.back() == 0) C.pop_back();    return C;&#125;int main()&#123;    string a;    int b;    cin &gt;&gt; a &gt;&gt; b;    vector&lt;int&gt; A;    for(int i = a.size()-1; i &gt;= 0; i--) A.push_back(a[i] - &#x27;0&#x27;);        int r = 0;    vector&lt;int&gt; C = div(A, b, r);        for(int i = C.size()-1; i &gt;= 0; i--) cout &lt;&lt; C[i];    cout &lt;&lt; endl &lt;&lt; r;    return 0;&#125;



前缀和与差分
AcWing 795. 前缀和
#include&lt;iostream&gt;using namespace std;const int N = 1e6+10;int a[N], s[N];int main()&#123;        int n, m;    scanf(&quot;%d%d&quot;, &amp;n, &amp;m);    for(int i = 1; i &lt;= n; i++) cin &gt;&gt; a[i];    for(int i = 1; i &lt;= n; i++) s[i] = s[i-1] + a[i];    while(m--)&#123;        int l, r;        scanf(&quot;%d%d&quot;, &amp;l, &amp;r);        cout &lt;&lt; s[r] - s[l-1] &lt;&lt; endl;    &#125;        return 0;&#125;


AcWing 796. 子矩阵的和

#include&lt;iostream&gt;using namespace std;const int N = 1010;int a[N][N], s[N][N];int n,m,q;int main()&#123;        cin &gt;&gt; n &gt;&gt; m &gt;&gt; q;    for(int i = 1;i&lt;=n;i++)        for(int j = 1;j&lt;=m;j++)            cin &gt;&gt; a[i][j], s[i][j] = a[i][j] + s[i-1][j] + s[i][j-1] - s[i-1][j-1];         while(q--)&#123;        int x1, y1, x2, y2;        cin &gt;&gt; x1 &gt;&gt; y1 &gt;&gt; x2 &gt;&gt; y2;        cout &lt;&lt; s[x2][y2] - s[x1 - 1][y2] - s[x2][y1 - 1] + s[x1 - 1][y1 - 1] &lt;&lt; endl;;    &#125;    return 0;&#125;




AcWing 797. 差分

#include&lt;iostream&gt;using namespace std;const int N = 100010;int a[N], b[N];int n, m ;void insert(int l, int r, int c)&#123;    b[l] += c;    b[r + 1] -= c;&#125;int main()&#123;    cin &gt;&gt; n &gt;&gt; m;    for(int i = 1; i &lt;= n; i++) cin &gt;&gt; a[i];    for(int i = 1; i &lt;= n; i++) insert(i, i, a[i]);    while(m--)&#123;        int l, r, c;        cin &gt;&gt; l &gt;&gt; r &gt;&gt; c;        insert(l, r, c);    &#125;    for(int i = 1; i &lt;= n;i++ ) a[i] = a[i-1] + b[i], cout &lt;&lt; a[i] &lt;&lt; &quot; &quot;;    &#125;




AcWing 798. 差分矩阵

#include&lt;iostream&gt;using namespace std;const int N = 1010;int a[N][N], b[N][N];int n, m, q;void insert(int x1, int y1, int x2, int y2, int c)&#123;    b[x1][y1] += c;    b[x1][y2 + 1] -= c;    b[x2 + 1][y1] -= c;    b[x2 + 1][y2 + 1] += c;&#125;int main()&#123;        cin &gt;&gt; n &gt;&gt; m &gt;&gt; q;    for(int i = 1; i &lt;= n; i++)        for(int j = 1; j &lt;= m; j++) cin &gt;&gt; a[i][j];        for(int i = 1; i &lt;= n; i++)        for(int j = 1; j &lt;= m; j++) insert(i, j, i, j, a[i][j]);        while(q--)&#123;        int x1, y1, x2, y2, c;        cin &gt;&gt; x1 &gt;&gt; y1 &gt;&gt; x2 &gt;&gt; y2 &gt;&gt; c;        insert(x1, y1, x2, y2, c);    &#125;    for(int i = 1; i&lt;= n; i++)&#123;        for(int j = 1;j &lt;= m; j++)&#123;            a[i][j] = b[i][j] + a[i-1][j] + a[i][j-1] - a[i-1][j-1];            cout &lt;&lt; a[i][j] &lt;&lt; &quot; &quot;;        &#125;        cout &lt;&lt; endl;    &#125;        return 0;&#125;



双指针算法
AcWing 799. 最长连续不重复子序列

#include&lt;iostream&gt;using namespace std;const int N = 100010;int a[N], s[N];int n;int main()&#123;    cin &gt;&gt; n;    for(int i = 0; i&lt;n; i++) cin &gt;&gt; a[i];    int res = 0;    for(int i = 0, j = 0; i &lt; n; i ++)&#123;        s[a[i]]++;        while(s[a[i]] &gt; 1)&#123;            s[a[j]]--;            j++;        &#125;        res = max(res, i - j + 1);    &#125;    cout &lt;&lt; res &lt;&lt; endl;    return 0;&#125;




AcWing 800. 数组元素的目标和 

#include&lt;iostream&gt;using namespace std;const int N = 100010;int a[N], b[N];int n, m, x;int main()&#123;        cin &gt;&gt; n &gt;&gt; m &gt;&gt; x;    for(int i = 0;i&lt;n;i++) cin &gt;&gt; a[i];    for(int i = 0;i&lt;m;i++) cin &gt;&gt; b[i];        //双指针    for(int i=0, j=m-1;i&lt;n;i++)&#123;        while(j &gt;=0  &amp;&amp; a[i]+b[j] &gt; x) j--;        if(a[i] + b[j] == x)&#123;            cout &lt;&lt; i &lt;&lt; &quot; &quot; &lt;&lt; j &lt;&lt; endl;            break;        &#125;    &#125;        return 0;&#125;




AcWing 2816. 判断子序列

#include&lt;iostream&gt;using namespace std;const int N = 100010;int A[N], B[N];int n, m;int main()&#123;    cin &gt;&gt; n &gt;&gt; m;    for(int i=0;i&lt;n;i++) cin &gt;&gt; A[i];    for(int i=0;i&lt;m;i++) cin &gt;&gt; B[i];    // int i = 0;    // for(int j = 0;j&lt;m;j++)&#123;    //     if(i&lt;n &amp;&amp; A[i] == B[j]) i++;    // &#125;    // if(i == n) cout &lt;&lt; &quot;Yes&quot; &lt;&lt; endl;    // else cout &lt;&lt; &quot;No&quot; &lt;&lt; endl;    int i, j=0;    for(i=0;i&lt;n;i++)&#123;        while(j &lt; m &amp;&amp; A[i] != B[j]) j++;        if(j == m) break;        else j++;    &#125;    if(i == n) puts(&quot;Yes&quot;);    else puts(&quot;No&quot;);        return 0;&#125;



位运算
AcWing 801. 二进制中1的个数

#include&lt;iostream&gt;using namespace std;const int N = 100010;int a[N], res[N];int lowbit(int &amp;x)&#123;    return x&amp;-x;&#125;int main()&#123;        int n;    cin &gt;&gt; n;    for(int i = 0;i &lt; n; i++) cin &gt;&gt; a[i];        for(int i=0;i&lt;n;i++)&#123;        int res = 0;        while(a[i]) a[i] -= lowbit(a[i]), res++;        cout &lt;&lt; res &lt;&lt; &quot; &quot;;    &#125;    cout &lt;&lt; endl;        return 0;&#125;



离散化
AcWing 802. 区间和

#include &lt;iostream&gt;#include &lt;vector&gt;#include &lt;algorithm&gt;using namespace std;const int N = 300010;int a[N], s[N];typedef pair&lt;int, int&gt; PII;vector&lt;int&gt; alls;vector&lt;PII&gt; adds, quires;int find(int x)&#123;    int l = 0, r = alls.size() - 1;    while (l &lt; r)&#123;        int mid = l + r &gt;&gt; 1;        if (alls[mid] &gt;= x) r = mid;        else l = mid + 1;    &#125;    return l + 1;&#125;int main()&#123;    int n, m;    cin &gt;&gt; n &gt;&gt; m;        for (int i = 0; i &lt; n; i ++ )&#123;        int x, c;        cin &gt;&gt; x &gt;&gt; c;        alls.push_back(x);        adds.push_back(&#123;x, c&#125;);    &#125;    for (int i = 0; i &lt; m; i ++ )&#123;        int l, r;        cin &gt;&gt; l &gt;&gt; r;        quires.push_back(&#123;l, r&#125;);        alls.push_back(l);        alls.push_back(r);    &#125;    // 去重    sort(alls.begin(), alls.end());    alls.erase(unique(alls.begin(), alls.end()), alls.end());        for (auto item : adds)&#123;        int x = find(item.first);        a[x] += item.second;    &#125;        for (int i = 1; i &lt;= alls.size(); i ++ ) s[i] = s[i - 1] + a[i];        for (auto item : quires)&#123;        int l = find(item.first);        int r = find(item.second);        cout &lt;&lt; s[r] - s[l - 1] &lt;&lt; endl;    &#125;    return 0;&#125;



区间合并
AcWing 803. 区间合并

#include&lt;iostream&gt;#include&lt;vector&gt;#include&lt;algorithm&gt;using namespace std;typedef pair&lt;int,int&gt; PII;const int N = 100010;int n;int main()&#123;    cin &gt;&gt; n;    vector&lt;PII&gt; segs;    vector&lt;PII&gt; res;    while(n--)&#123;        int l, r;        cin &gt;&gt; l &gt;&gt; r;        segs.push_back(&#123;l,r&#125;);    &#125;        sort(segs.begin(), segs.end());    int st = -2e9, ed = -2e9;    for(auto seg : segs)&#123;        if(ed &lt; seg.first)&#123;            if(st!=-2e9) res.push_back(&#123;st, ed&#125;);            st = seg.first, ed = seg.second;        &#125;        else            ed = max(ed, seg.second);    &#125;    if(st!=2e9) res.push_back(&#123;st, ed&#125;);    cout &lt;&lt; res.size() &lt;&lt; endl;        return 0;&#125;

单链表
AcWing 826. 单链表

#include&lt;iostream&gt;using namespace std;const int N = 100010;int e[N], ne[N], head, idx;void init()&#123;    head = -1;    idx = 0;&#125;void add_head(int x)&#123;        e[idx] = x, ne[idx] = head, head = idx ++;&#125;void insert(int k, int x)&#123;    e[idx] = x;    ne[idx] = ne[k];    ne[k] = idx++;&#125;void remove(int k)&#123;        ne[k] = ne[ne[k]];&#125;int main()&#123;        init();    int m;    cin &gt;&gt; m;    while(m --)&#123;        char op;        int k, x;        cin &gt;&gt; op;        if(op == &#x27;H&#x27;)&#123;            cin &gt;&gt; x;            add_head(x);        &#125;                if(op == &#x27;I&#x27;)&#123;            cin &gt;&gt; k &gt;&gt; x;            insert(k-1, x);        &#125;                if(op == &#x27;D&#x27;)&#123;            cin &gt;&gt; k;            if(!k) head = ne[head];            else remove(k-1);        &#125;    &#125;    for(int i=head; i!=-1;i=ne[i])        cout &lt;&lt; e[i] &lt;&lt; &#x27; &#x27;;        return 0;&#125;



双链表
AcWing 827. 双链表

#include&lt;iostream&gt;using namespace std;const int N = 100010;int e[N], l[N], r[N], idx;void insert(int k, int x)&#123;    // k的右侧插入x    e[idx] = x;    l[idx] = k, r[idx] = r[k];    l[r[k]] = idx, r[k] = idx++;&#125;void remove(int k)&#123;    l[r[k]] = l[k];    r[l[k]] = r[k];&#125;int main()&#123;       r[0] = 1, l[1] = 0;    idx = 2;    int m;    cin &gt;&gt; m;    while(m--)&#123;        string op;        int k, x;        cin &gt;&gt; op;        if(op == &quot;L&quot;)&#123;            cin &gt;&gt; x;            insert(0, x);        &#125;        if(op == &quot;R&quot;)&#123;            cin &gt;&gt; x;            insert(l[1], x);        &#125;        if(op == &quot;D&quot;)&#123;            cin &gt;&gt; k;            remove(k + 1);        &#125;        if(op == &quot;IL&quot;)&#123;            cin &gt;&gt; k &gt;&gt; x;            insert(l[k + 1], x);        &#125;        if(op == &quot;IR&quot;)&#123;            cin &gt;&gt; k &gt;&gt; x;            insert(k + 1, x);        &#125;    &#125;    for(int i = r[0]; i != 1; i = r[i])&#123;        // cout &lt;&lt; i &lt;&lt; &#x27; &#x27;;        cout &lt;&lt; e[i] &lt;&lt; &#x27; &#x27;;        // break;    &#125;            cout &lt;&lt; endl;    return 0;&#125;



栈
AcWing 828. 模拟栈 

#include &lt;iostream&gt;#include &lt;cstring&gt;#include &lt;algorithm&gt;using namespace std;const int N = 100010;int stk[N], tt=0;int main()&#123;    int m;    cin &gt;&gt; m;    while (m -- )&#123;        string op;        int x;        cin &gt;&gt; op;        if(op == &quot;push&quot;)&#123;            cin &gt;&gt; x;            stk[++tt] = x;        &#125;        if(op == &quot;pop&quot;)&#123;            tt--;        &#125;        if(op == &quot;empty&quot;)&#123;            if(tt &gt; 0)                cout &lt;&lt; &quot;NO&quot; &lt;&lt; endl;            else                cout &lt;&lt; &quot;YES&quot; &lt;&lt; endl;        &#125;        if(op == &quot;query&quot;)&#123;            cout &lt;&lt; stk[tt] &lt;&lt; endl;        &#125;            &#125;        return 0;&#125;




AcWing 3302. 表达式求值 (不会写)

